import AlertMessage from "./components/Utilities/Alerts/AlertMessage";
import Loader from "./components/Utilities/Loaders/Loader";
import ModalRemove from "./components/Utilities/Modals/ModalRemove";

export default {
    name: "App",
    components: {
        AlertMessage,
        Loader,
        ModalRemove
    },
    data() {
        return {
            productTemp: {
                preposition: 'el',
                typeElement: 'producto',
                mainNameElement: '',
                _id: null,
                element: {}
            },
            headers: [
                  {
                  text: "Medias y calcetines",
                  align: "left",
                  sortable: false,
                  value: "name"
                  },
                  { text: "Imagen", 
                  align: "left",
                  sortable: false,
                  value: "imagen" },
                  { text: "Marca", value: "marca" },
                  { text: "Familia de producto", value: "familiadeproducto" },
                  { text: "Categoría", value: "categoria" },
                  { text: "Referencia", value: "referencia" },
                  { text: "Producto", value: "producto" },
                  { text: "Color", value: "color" },
                  { text: "Talla CHica (cajas)", value: "chica" },
                  { text: "Talla MEDiana (cajas)", value: "mediana" },
                  { text: "Talla GranDE (cajas)", value: "grande" },
                  { text: "Talla Extra Grande (cajas)", value: "extragrande" },
                  { text: "Precio antes de IVA (Pesos MX)", value: "precio" },
                  { text: "Precio por partida (Pesos MX)", value: "precioxpartida" },

              ],
              partidas: [
                  {
                      id: 1,
                      imagen:"",
                      marca: "Medivaric",
                      familiadeproducto: "Media",
                      categoria: "Baja compresion 8-15 mmHg",
                      referencia: "MV-1111p",
                      producto:"Pantimedia dama transparente",
                      chica:0,
                      mediana:0,
                      grande:0,
                      extragrande:0,
                      precio:240,
                      precioxpartida:0
                  },
              ],
        }
    },
    methods: {
          save(item,key) {
              this.$refs.refAlertMessage.showAlertFull("star", "success",
                "Guardado", '', 5000, '', 'bottom', "Se ha guardado su dato");
              console.log(item[12]);
              },
          cancel(item,key) {},
          open(item,key) {
              alert(item[8]);
          },
          close(item,key) {},
        openAlert() {
            this.$refs.refAlertMessage.showAlertFull("star", "success",
                "Logro desbloqueado", '', 5000, '', 'bottom', "Has pasado al siguiente nivel!!");
        },
        openLoader() {
            this.$refs.refLoader.activate();
            setTimeout(() => {
                this.$refs.refLoaderTest.deactivate();
            }, 3000);
        },
        openModalRemove() {
            const product = {
                _id: "asdf1234",
                name: "iPhone 5",
                serialNumber: "XXX123123",
                price: 10000,
                brand: "Apple"
            };
            this.productTemp.element = product;
            this.productTemp._id = product._id;
            this.productTemp.mainNameElement = product.name;
            this.$refs.refModalRemove.dialog = true;
        },
        deleteUser(idUser) {
            console.log("Id del usuario a eliminar: ", idUser);
        }
    }
}
